package com.vanniktech.emoji.sample;

import android.annotation.SuppressLint;
import android.graphics.PorterDuff;
import android.os.Bundle;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.CheckBox;
import android.widget.ImageView;

import androidx.appcompat.app.AppCompatActivity;
import androidx.core.content.ContextCompat;
import androidx.emoji.text.EmojiCompat;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.vanniktech.emoji.EmojiEditText;
import com.vanniktech.emoji.material.MaterialEmojiLayoutFactory;
import com.vanniktech.emoji_stickers.EmojiPopupSticker;
import com.vanniktech.emoji_stickers.stickers.Sticker;
import com.vanniktech.emoji_stickers.stickers.StickerListener;
import com.vanniktech.emoji_stickers.stickers.StickerSettings;

import java.util.ArrayList;
import java.util.List;

import static android.view.View.GONE;
import static android.view.View.VISIBLE;

// We don't care about duplicated code in the sample.
@SuppressWarnings("CPD-START") public class StickersActivity extends AppCompatActivity {
    static final String TAG = "StickersActivity";

    ChatAdapterStickers chatAdapter;
    EmojiPopupSticker emojiPopupSticker;

    EmojiEditText editText;
    ViewGroup rootView;
    ImageView emojiButton;
    EmojiCompat emojiCompat;

    @Override @SuppressLint("SetTextI18n") protected void onCreate(final Bundle savedInstanceState) {
        getLayoutInflater().setFactory2(new MaterialEmojiLayoutFactory((LayoutInflater.Factory2) getDelegate()));
        super.onCreate(savedInstanceState);

        setContentView(R.layout.activity_main);


        chatAdapter = new ChatAdapterStickers();

        final Button button = findViewById(R.id.main_activity_material_button);
        button.setText("\uD83D\uDE18\uD83D\uDE02\uD83E\uDD8C");
        editText = findViewById(R.id.main_activity_chat_bottom_message_edittext);
        rootView = findViewById(R.id.main_activity_root_view);
        emojiButton = findViewById(R.id.main_activity_emoji);
        final ImageView sendButton = findViewById(R.id.main_activity_send);

        emojiButton.setColorFilter(ContextCompat.getColor(this, R.color.emoji_icons), PorterDuff.Mode.SRC_IN);
        sendButton.setColorFilter(ContextCompat.getColor(this, R.color.emoji_icons), PorterDuff.Mode.SRC_IN);

        final CheckBox forceEmojisOnly = findViewById(R.id.main_activity_force_emojis_only);
        forceEmojisOnly.setText("Force emojis only \uD83D\uDE18");
        forceEmojisOnly.setOnCheckedChangeListener((ignore, isChecked) -> {
            if (isChecked) {
                editText.clearFocus();
                emojiButton.setVisibility(GONE);
                editText.disableKeyboardInput(emojiPopupSticker);
            } else {
                emojiButton.setVisibility(VISIBLE);
                editText.enableKeyboardInput();
            }
        });

        emojiButton.setOnClickListener(ignore -> emojiPopupSticker.toggle());

        sendButton.setOnClickListener(ignore -> {
            final String text = editText.getText().toString().trim();

            if (text.length() > 0) {
                chatAdapter.add(new AdapterItem(null,text));

                editText.setText("");
            }
        });

        final RecyclerView recyclerView = findViewById(R.id.main_activity_recycler_view);
        recyclerView.setAdapter(chatAdapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this, RecyclerView.VERTICAL, false));

        setUpEmojiPopup();
    }



    private void setUpEmojiPopup() {
        List<Sticker> stickerList = new ArrayList<>();

        stickerList.add(new Sticker("data:image/jpeg;base64,"));
        stickerList.add(new Sticker("data:image/jpeg;base64,"));
        stickerList.add(new Sticker("data:image/jpeg;base64,"));
        stickerList.add(new Sticker(R.drawable.emoji_sticker_tab));

        emojiPopupSticker = EmojiPopupSticker.Builder.fromRootView(rootView)
                .setOnEmojiBackspaceClickListener(ignore -> Log.d(TAG, "Clicked on Backspace"))
                .setOnEmojiClickListener((ignore, ignore2) -> Log.d(TAG, "Clicked on emoji"))
                .setOnEmojiPopupShownListener(() -> emojiButton.setImageResource(R.drawable.ic_keyboard))
                .setOnSoftKeyboardOpenListener(ignore -> Log.d(TAG, "Opened soft keyboard"))
                .setOnEmojiPopupDismissListener(() -> emojiButton.setImageResource(R.drawable.emoji_ios_category_smileysandpeople))
                .setOnSoftKeyboardCloseListener(() -> Log.d(TAG, "Closed soft keyboard"))
                .setKeyboardAnimationStyle(R.style.emoji_fade_animation_style)
                .setPageTransformer(new PageTransformer())
                .setStickerSettings(new StickerSettings.Builder().stickers(stickerList).listener(new StickerListener() {
                    @Override
                    public void onClick(Sticker sticker) {
                        chatAdapter.add(new AdapterItem(sticker,null));

                    }

                    @Override
                    public void onLongClick(Sticker sticker) {
                        Log.d(TAG,"onLongClick sticker "+sticker.toString());
                    }
                }).build())
                .build(editText);
    }
}
